function getXMLHttpRequest(where){var xmlHttp;try{xmlHttp=new XMLHttpRequest()}catch(e){try{xmlHttp=new ActiveXObject("Msxml2.XMLHTTP")}catch(e){try{xmlHttp=new ActiveXObject("Microsoft.XMLHTTP")}catch(e){alert("Your browser does not support AJAX!");return!1}}}
xmlHttp.output=where;return xmlHttp}
function stateChanged(obj){document.getElementById(obj.output).innerHTML="<img src=\"images/ajax-loader.gif\" alt=\"\" title=\"Processing...\">";if(obj.readyState==4){document.getElementById(obj.output).innerHTML=xmlHttp.responseText;delete obj}}
function ajaxCheckPath(reldir,url,where){var url="helpers/ajax_checkpath.php?reldir="+reldir+"&url="+url;xmlHttp=getXMLHttpRequest(where);if(xmlHttp==!1){alert("No Ajax possible!");return}
if(xmlHttp.overrideMimeType){xmlHttp.overrideMimeType('text/xml')}
xmlHttp.callback=function(){stateChanged(xmlHttp)};xmlHttp.onreadystatechange=xmlHttp.callback;xmlHttp.open("POST",url,!0);xmlHttp.send(null)}
function ajaxCheckDB(server,user,pass,db,prefix,where){var url="helpers/ajax_checkdb.php?server="+server+"&user="+user+"&pass="+pass+"&db="+db+"&prefix="+prefix;xmlHttp=getXMLHttpRequest(where);if(xmlHttp===!1){alert("No Ajax possible!");return}
if(xmlHttp.overrideMimeType){xmlHttp.overrideMimeType('text/xml')}
xmlHttp.callback=function(){stateChanged(xmlHttp)};xmlHttp.onreadystatechange=xmlHttp.callback;xmlHttp.open("POST",url,!0);xmlHttp.send(null)}